import React from "react";
import { Grid } from "@mui/material";
import { IProduct } from "../context/product";
import Container from "@mui/material/Container";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import "./ProductContent.css";
type ProductProps = {
  products: IProduct[];
};

function createData(
  id: string,
  brand: string,
  category: string,
  description: string,
  discountPercentage: number,
  price: number,
  rating: number,
  stock: number,
  images: string,
  thumbnail: string,
  title: string
) {
  return {
    id,
    brand,
    category,
    description,
    discountPercentage,
    price,
    rating,
    stock,
    images,
    thumbnail,
    title,
  };
}

const Product: React.FC<ProductProps> = ({ products }) => {
  console.log("products", products);
  const truncateText = (text, maxLength) => {
    if (text.length <= maxLength) {
      return text;
    } else {
      return text.substring(0, maxLength) + '...';
    }
  };
  return (
    // <Container maxWidth="xl">
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <TableContainer component={Paper}>
          <Table aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Id</TableCell>
                <TableCell align="right">Brand</TableCell>
                <TableCell align="right">Title</TableCell>
                <TableCell align="right">Category</TableCell>
                <TableCell align="right">Description</TableCell>
                <TableCell align="right">DiscountPercentage</TableCell>
                <TableCell align="right">Price</TableCell>
                <TableCell align="right">Rating</TableCell>
                <TableCell align="right">Stock</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {products.map((row) => (
                <TableRow
                  key={row.id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.id}
                  </TableCell>
                  <TableCell align="right">{row.title}</TableCell>
                  <TableCell align="right">{row.brand}</TableCell>
                  <TableCell align="right">{row.category}</TableCell>
                  <TableCell align="right">{truncateText(row.description, 50)}</TableCell>
                  <TableCell align="right">{row.discountPercentage}</TableCell>
                  <TableCell align="right">{row.price}</TableCell>
                  <TableCell align="right">{row.rating}</TableCell>
                  <TableCell align="right">{row.stock}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
    // </Container>
  );
};

export default Product;
