import React, { useState, useCallback } from "react";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { getProductsAction } from "../context/productAction";
import { ProductContext } from "../context/productContext";

const SearchComponent = () => {
  const { dispatch } = React.useContext(ProductContext);

  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = useCallback(() => {
    getProductsAction(dispatch, { search: searchQuery });
  }, [searchQuery]);

  return (
    <Box
      display="flex"
      justifyContent="space-between"
      alignItems="center"
      style={{ height: 40 }}
    >
      {/* <input
        value={searchQuery}
        data-testid="search-input"
        onChange={(e) => setSearchQuery(e.target.value)}
      /> */}
      <TextField
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        data-testid="search-input" // Adding data-testid directly to TextField
        style={{
          // Input styles here
          width: "70%",
          padding: "5px",
          border: "1px solid #ccc",
          borderRadius: "4px",
        }}
      />
      {/* <Button
        variant="contained"
        color="primary"
        onClick={handleSearch}
        data-testid="search-button"
      >
        Search
      </Button> */}
      <Button
        variant="contained"
        color="primary"
        onClick={handleSearch}
        data-testid="search-button"
        sx={{
          // Button styles here
          backgroundColor: "#007bff",
          color: "#fff",
          borderRadius: "4px",
          "&:hover": {
            backgroundColor: "#0056b3",
          },
        }}
      >
        Search
      </Button>
    </Box>
  );
};

export default SearchComponent;
