import React from "react";
import axios from "axios";
import { ProductTypes, ProductActions } from "./productReducer";
import { IProduct,ror } from "./product";

export const getProductsAction = (
  dispatch: React.Dispatch<ProductActions>,
  { search }: { search?: string }
) => {
  axios({
    url: `https://dummyjson.com/prots${search ? `/search?q=${search}` : ""}`,
  })
    .then(
      (result: {
        data: {
          limit: number;
          products: IProduct[];
          skip: number;
          total: number;
        };
      }) => {
        dispatch({
          type: ProductTypes.GET,
          payload: result.data.products,
        });
      }
    )
    .catch((error) => {
      console.error("An error occurred:", error);
      // You can dispatch an error action or handle the error as needed.
    });
};
