import * as React from "react";
import { AuthContextType, ISession, IUser } from "./auth";

export const AuthContext = React.createContext<AuthContextType | null>(null);

const AuthProvider: React.FC<React.PropsWithChildren> = ({ children }) => {
  const [users, setUser] = React.useState<IUser[]>([]);
  const [session, setSession] = React.useState<ISession | null>(null);

  React.useEffect(() => {
    localStorage.getItem("context");
  }, [])

  const createUser = (user: IUser) => {
    setUser([...users, user]);
    setSession({ email: user.email, expiredAt: getSessionExpireDate() });
  };

  const getSessionExpireDate = () => {
    const date = new Date();
    date.setFullYear(date.getFullYear() + 1);
    return date;
  };

  const login = (email: string, password: string) => {
    const findMatch = users.find(
      (user) => user.email === email && user.password === password
    );
    if (findMatch) {
      setSession({ email: findMatch.email, expiredAt: getSessionExpireDate() });
    }
  };

  const logout = () => {
    setSession(null);
  };

  return (
    <AuthContext.Provider value={{ users, session, createUser, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
