import * as React from "react";
import {
  AuthContextType,
  ISession,
  IUser,
  AuthContextStorageType,
} from "./auth";
import { AuthState, authReducer, AuthActions } from "./authReducer";


type InitialStateType = {
  auth: AuthState;
};

const mainReducer = ({ auth }: InitialStateType, action: AuthActions) => ({
  auth: authReducer(auth, action),
});

const initialState = {
  auth: {
    users: [],
    session: null,
  },
};

export const AuthContext = React.createContext<{
  state: InitialStateType;
  dispatch: React.Dispatch<AuthActions>;
}>({
  state: initialState,
  dispatch: () => null,
});

const AuthProvider: React.FC<React.PropsWithChildren> = ({ children }) => {
  const [state, dispatch] = React.useReducer(mainReducer, initialState);

  React.useEffect(() => {
    const context = getStorageContext();

    if (context) {
      setUser(context.users);
      setSession(context.session);
    }
  }, []);

  const logout = () => {
    localStorage.setItem(
      CONTEXT_STORAGE_KEY,
      JSON.stringify({ users, session: null })
    );
  };

  return (
    <AuthContext.Provider value={{ state, createUser, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
