import { IUser, ISession, ActionMap } from "./auth";

export enum AuthTypes {
  CREATE = "CREATE_USER",
  LOGIN = "LOGIN",
  LOGOUT = "LOGOUT",
}

export const CONTEXT_STORAGE_KEY = "auth";

const getSessionExpireDate = () => {
  const date = new Date();
  date.setFullYear(date.getFullYear() + 1);
  return date;
};

type AuthPayload = {
  [AuthTypes.CREATE]: IUser;
  [AuthTypes.LOGIN]: {
    email: string;
    password: string;
  };
  [AuthTypes.LOGOUT]: {};
};

export type AuthState = {
  users: IUser[];
  session: ISession | null;
};

export type AuthActions = ActionMap<AuthPayload>[keyof ActionMap<AuthPayload>];

export const authReducer = (state: AuthState, action: AuthActions) => {
  switch (action.type) {
    case AuthTypes.CREATE:
      const newUsers = [...state.users, action.payload];
      const newSession = {
        email: action.payload.email,
        expiredAt: getSessionExpireDate(),
      };

      localStorage.setItem(
        CONTEXT_STORAGE_KEY,
        JSON.stringify({ users: newUsers, session: newSession })
      );

      return;
    case AuthTypes.Delete:
      return [...state.filter((product) => product.id !== action.payload.id)];
    default:
      return state;
  }
};
