import { IData, ProductContextStorageType, ActionMap } from "./auth";

export enum ProductTypes {
  GET = "GET",
}


const getSessionExpireDate = () => {
  const date = new Date();
  date.setFullYear(date.getFullYear() + 1);
  return date;
};

export const getStorageContext = () => {
  const raw = localStorage.getItem(CONTEXT_STORAGE_KEY);
  return raw ? (JSON.parse(raw) as ProductContextStorageType) : undefined;
};

export type ProductState = {
  data: IData[];
};

type ProductPayload = {
  [ProductTypes.GET]: IData;
};

export type ProductActions =
  ActionMap<ProductPayload>[keyof ActionMap<ProductPayload>];

export const ProductReducer = (state: ProductState, action: ProductActions) => {
  switch (action.type) {
    case ProductTypes.GET: {
      const newState = {
        data: [...state.data, action.payload],
      };
      return newState;
    }

    default:
      return state;
  }
};
